import vueLoginTool from './login-tool.vue'

const vueLoginToolPackage = {
  install(Vue) {
    Vue.component(vueLoginTool.name, vueLoginTool)
  }
}

if (typeof window !== undefined && window.Vue) {
  window.Vue.use(vueLoginToolPackage)
}
export default vueLoginToolPackage